button = (data, filename = 'data.csv', displayname) =&gt; {
  const downloadData = new Blob([d3.csvFormat(data)], { type: "text/csv" });

  const size = (downloadData.size / 1024).toFixed(1);
  const button = DOM.download(
    downloadData,
    filename,
    `${displayname} (${size} KB)`
  );
  return button;
}








